import React,{useState} from 'react';
import axios from 'axios';
export default function HabitForm(){
  const[habit,setHabit]=useState({name:'',frequency:'',category:'',start_date:''});
  const handleChange=e=>setHabit({...habit,[e.target.name]:e.target.value});
  const handleSubmit=async e=>{e.preventDefault();await axios.post('http://localhost:8000/api/habits/',habit);alert('Habit Created!');};
  return(<form onSubmit={handleSubmit}>
    <input name="name" placeholder="Habit Name" onChange={handleChange} required/>
    <select name="frequency" onChange={handleChange}><option value="">Frequency</option><option value="daily">Daily</option><option value="weekly">Weekly</option></select>
    <select name="category" onChange={handleChange}><option value="">Category</option><option value="health">Health</option><option value="work">Work</option><option value="learning">Learning</option></select>
    <input type="date" name="start_date" onChange={handleChange} required/>
    <button type="submit">Add Habit</button></form>);
}
